mapper.select:
/*게시글 목록 조회*/ SELECT * FROM notice_board AS nb INNER JOIN board_category AS bc ON nb.bctcode=bc.bctcode WHERE bc.bct_value = #{boardCateValue}
AND nb.${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY CAST(SUBSTRING_INDEX(nb.nbcode, 'nb', -1) AS SIGNED) LIMIT #{startRow}, #{rowPerPage};

	/* 게시글코드 도출*/
	SELECT 
    	MAX(CAST(REGEXP_REPLACE(nbcode, '[^0-9]', '') AS UNSIGNED)) AS max_number
	FROM 
    	notice_board;

mapper.update:
		/* 게시글 수정*/
		UPDATE
			notice_board
		SET
			nb_title=#{nboardTitle},
			nb_content=#{nboardContent}
		 WHERE
        	nbcode = #{nboardCode};
