mapper.select:
/* 로그인 테이블 행의 갯수 조회 */
SELECT
	COUNT(1)
FROM
	tb_login;

/* 회원조회 */



		${searchKey} LIKE CONCAT('%', #{searchValue}, '%')


ORDER BY m.id

mapper.delete:
/* 회원탈퇴 */
DELETE
FROM
	tb_member
WHERE 
	m_id = #{memberId};

/* 회원 로그인 이력 삭제 */
DELETE 
FROM
	tb_login
WHERE
	login_id = #{memberId};

mapper.update:
/* 회원수정 */
UPDATE tb_member


		m_pw = #{memberPw},


		m_name = #{memberName},


		m_level = #{memberLevel},


		m_addr = #{memberAddr},


		m_email = #{memberEmail}


WHERE 
	m_id = #{memberId}
